window.addEventListener("DOMContentLoaded",()=>{window.addEventListener("scroll",()=>{const header=document.querySelector("header");header.classList.toggle("sticky",window.scrollY>150)});const hamburger=document.querySelector(".hamburger"),menyu=document.querySelector(".menyu"),menyuItems=document.querySelectorAll(".menyu a");hamburger.addEventListener("click",()=>{hamburger.classList.toggle("active"),menyu.classList.toggle("active")}),menyuItems.forEach(navItem=>{navItem.addEventListener("click",()=>{hamburger.classList.add("active"),menyu.classList.add("active")})})});document.addEventListener('copy', evt =>{evt.preventDefault();evt.clipboardData.setData('text/plain', 'text to clipboard');});